fn main() {
    let impl_bytecode = "";
    let hex_str = impl_bytecode.trim_start_matches("0x");
    println!("Length: {}", hex_str.len());
    if hex_str.len() % 2 != 0 {
        println!("Odd length!");
    }
    match hex::decode(hex_str) {
        Ok(_) => println!("Decode success"),
        Err(e) => println!("Decode error: {:?}", e),
    }
}
