use std::fs::File;
use std::io::Write;

fn main() {
    // Full creation bytecode with constructor args for "TestNFT" "TNFT"
    let bytecode = "";

    // Convert hex to bytes
    let bytes = hex::decode(bytecode).expect("Failed to decode hex");

    // Save to file
    let mut file = File::create("nft_bytecode.bin").expect("Failed to create file");
    file.write_all(&bytes).expect("Failed to write bytes");
    println!("Saved {} bytes to nft_bytecode.bin", bytes.len());
}
